import React, { useState } from 'react';

const questions = [
  {
    type: 'mcq',
    question: 'What is the capital of France?',
    options: ['Paris', 'London', 'Berlin', 'Madrid'],
    answer: 'Paris',
  },
  {
    type: 'text',
    question: 'What is 2 + 2?',
    answer: '4',
  },
  // Add more questions as needed
];

const QuizComponent = () => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [userAnswers, setUserAnswers] = useState({});
  const [showResults, setShowResults] = useState(false);

  const handleNext = () => {
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      setShowResults(true);
    }
  };

  const handleAnswerChange = (e) => {
    setUserAnswers({
      ...userAnswers,
      [currentQuestionIndex]: e.target.value,
    });
  };

  const calculateScore = () => {
    return questions.reduce((score, question, index) => {
      if (question.answer === userAnswers[index]) {
        return score + 1;
      }
      return score;
    }, 0);
  };

  return (

      <div className="flex items-center justify-center bg-white bg-opacity-75 backdrop-blur-sm p-6 rounded-lg shadow-lg max-w-md w-full">
        {!showResults ? (
          <div className="w-full">
            <div className="mb-4">
              <h2 className="text-xl font-bold mb-2">
                Question {currentQuestionIndex + 1}:
              </h2>
              <p className="text-lg">{questions[currentQuestionIndex].question}</p>
            </div>
            {questions[currentQuestionIndex].type === 'mcq' ? (
              <div className="flex flex-col mb-4">
                {questions[currentQuestionIndex].options.map((option, index) => (
                  <label key={index} className="mb-2">
                    <input
                      type="radio"
                      name={`question-${currentQuestionIndex}`}
                      value={option}
                      onChange={handleAnswerChange}
                      className="mr-2"
                    />
                    {option}
                  </label>
                ))}
              </div>
            ) : (
              <div className="mb-4">
                <input
                  type="text"
                  value={userAnswers[currentQuestionIndex] || ''}
                  onChange={handleAnswerChange}
                  className="w-full p-2 border rounded"
                />
              </div>
            )}
            <button
              onClick={handleNext}
              className="px-4 py-2 bg-blue-500 text-white rounded"
            >
              Next
            </button>
          </div>
        ) : (
          <div className="w-full">
            <h2 className="text-xl font-bold mb-4">Quiz Results</h2>
            <p className="text-lg mb-4">Your Score: {calculateScore()} / {questions.length}</p>
            <div>
              {questions.map((question, index) => (
                <div key={index} className="mb-4">
                  <p className="text-lg">
                    {index + 1}. {question.question}
                  </p>
                  <p className={`text-sm ${question.answer === userAnswers[index] ? 'text-green-500' : 'text-red-500'}`}>
                    Your Answer: {userAnswers[index]}
                  </p>
                  {question.answer !== userAnswers[index] && (
                    <p className="text-sm text-blue-500">Correct Answer: {question.answer}</p>
                  )}
                </div>
              ))}
            </div>
          </div>
        )}
      </div>

  );
};

export default QuizComponent;
