import React, { useState } from 'react'

export default function Interviewprep() {

  const [fileName, setFileName] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(true);

  const handleFileChange = (e) => {
    if (e.target.files.length > 0) {
      setFileName(e.target.files[0].name);
    }
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
  };

  const handleRemoveFile = () => {
    setFileName('');
  };

  return (
    <div className="flex justify-center items-center h-screen">
      <div className="flex flex-col sm:flex-row bg-gray-200 p-4 rounded-lg shadow-lg">

        <div className="bg-white p-4 m-2 rounded-lg shadow-md">
        <div className="fixed inset-0 flex items-center justify-center bg-gray-800 bg-opacity-50 z-50">
      <div className="bg-white rounded-lg shadow-xl max-w-xs w-full p-4 relative transition duration-300">
        <span className="flex items-center justify-center bg-red-600 h-8 w-8 rounded-full absolute top-2 right-2 text-white font-bold cursor-pointer">
          X
        </span>
        <div className="text-center">
          <span className="text-xl font-semibold">Upload a File</span>
          <p className="mt-2">Select a file to upload from your computer or device.</p>
        </div>
        <div className="mt-4 flex items-center justify-center">
          <label htmlFor="file" className="cursor-pointer bg-transparent border-dashed border-2 border-gray-500 p-2 w-full text-center">
            Choose File
            <input hidden type="file" id="file" onChange={handleFileChange} />
          </label>
        </div>
        {fileName && (
          <div className="mt-4 bg-blue-100 flex items-center relative rounded-lg p-2">
            <p className="font-light">{fileName}</p>
            <span className="absolute top-1 right-2 flex items-center justify-center bg-gray-800 h-8 w-8 rounded-full text-white font-bold cursor-pointer">
              X
            </span>
          </div>
        )}
      </div>
    </div>



        </div>

        <div className="bg-white p-4 m-2 rounded-lg shadow-md">Container 2





        </div>
      </div>
    </div>
  )
}
