import React from 'react';
import QuizComponent from './Question';

const QuestionLayout = () => {
  const questions = [
    "What is your name?",
    "Describe your experience with React.",
    "What is Tailwind CSS?",
    "Explain the difference between REST and GraphQL.",
    "How do you optimize a React application?",
    "What are the new features in ES6?",
    // Add more questions as needed
  ];

  return (
    <div className="min-h-screen bg-gray-100 p-4">
      <div className="flex flex-col lg:flex-row mt-20 lg:mt-24">
        <div className="w-full lg:w-1/3 p-4 overflow-y-auto h-96 bg-white shadow-md rounded mb-4 lg:mb-0 lg:mr-4">
          <h2 className="text-xl font-bold mb-4">Saved Questions</h2>
          <ul>
            {questions.map((question, index) => (
              <li key={index} className="p-2 border-b border-gray-300">
                {question}
              </li>
            ))}
          </ul>
        </div>
        <div className="w-full lg:w-2/3 p-4 bg-white shadow-md rounded">
          <h2 className="text-xl font-bold mb-4"> Quiz Questions</h2>
          <div class="radio-input flex flex-col p-3 bg-white text-black rounded-lg shadow-lg w-80">
  <div class="info flex items-center justify-between mb-2">
    <span class="question text-gray-800 text-base font-extrabold">What does CSS stand for?</span>
    <span class="steps bg-black text-white py-1 px-2 rounded text-xs font-semibold">3/10</span>
  </div>
  <input type="radio" id="value-1" name="value-radio" value="value-1" class="hidden"/>
  <label for="value-1" class="flex bg-white p-3 my-1 text-sm font-semibold rounded-lg cursor-pointer border border-gray-300 hover:bg-gray-200 transition ease-in-out duration-300">Computer Style Sheets</label>
  <input type="radio" id="value-2" name="value-radio" value="value-2" class="hidden"/>
  <label for="value-2" class="flex bg-white p-3 my-1 text-sm font-semibold rounded-lg cursor-pointer border border-gray-300 hover:bg-gray-200 transition ease-in-out duration-300">Cascading Style Sheets</label>
  <input type="radio" id="value-3" name="value-radio" value="value-3" class="hidden"/>
  <label for="value-3" class="flex bg-white p-3 my-1 text-sm font-semibold rounded-lg cursor-pointer border border-gray-300 hover:bg-gray-200 transition ease-in-out duration-300">Creative Style Sheets</label>
  <span class="result success hidden text-green-500 font-semibold text-xs mt-2">Congratulations!</span>
  <span class="result error hidden text-red-500 font-semibold text-xs mt-2">Bad answer</span>
</div>



        </div>
      </div>
    </div>
  );
};

export default QuestionLayout;
