import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';


const QuestionComponent = () => {
  const location = useLocation();
  // const { questions } = location.state || {};
  const {isQuestion} = location.state || {};
  console.log(isQuestion)
 let questions= [
    {
      question: 'Which two world powers were not initially involved in World War I but had a significant impact when they joined the conflict?',
      options: [
        'China and Japan',
        'United States and Soviet Union',
        'India and Brazil',
        'Canada and Australia'
      ],
      answer: 'United States and Soviet Union'
    },
    {
      question: 'True or False: World War I ended solely with the signing of the Treaty of Versailles?',
      options: [ 'True', 'False' ],
      answer: 'False',
      explanation: "While the Treaty of Versailles was a significant document that officially ended the war with Germany, it's important to note that multiple treaties and agreements were signed with other Central Powers, such as the Treaty of Saint-Germain with Austria, the Treaty of Trianon with Hungary, and the Treaty of SÃ¨vres (later replaced by the Treaty of Lausanne) with the Ottoman Empire."
    }
  ]
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [userAnswers, setUserAnswers] = useState({});
  const [showResults, setShowResults] = useState(false);
  const [showWarning, setShowWarning] = useState(false);

  const [isQuestionAvail, setisQuestionAvail] = useState(isQuestion);

  const handleNext = () => {
    if (!userAnswers[currentQuestionIndex]) {
      setShowWarning(true);
    } else {
      setShowWarning(false);
      if (currentQuestionIndex < questions.length - 1) {
        setCurrentQuestionIndex(currentQuestionIndex + 1);
      } else {
        setShowResults(true);
      }
    }
  };

  const handleSaveForLater = () => {
    // Implement save functionality
    alert('Saved for later practice');
  };

  const handleAnswerChange = (e) => {
    setUserAnswers({
      ...userAnswers,
      [currentQuestionIndex]: e.target.value,
    });
    setShowWarning(false); // Reset warning when user inputs an answer
  };

  const calculateScore = () => {
    return questions.reduce((score, question, index) => {
      if (question.answer === userAnswers[index]) {
        return score + 1;
      }
      return score;
    }, 0);
  };

  return (
    <>
    {isQuestionAvail ?
      <div className="flex items-center justify-center  bg-opacity-90 backdrop-blur-sm p-6 rounded-lg  max-w-md w-full">
        {!showResults ? (
          <div className="w-full">
            <div className="mb-4">
              <h2 className="text-xl font-bold mb-2">
                Question {currentQuestionIndex + 1}:
              </h2>
              <p className="text-lg">{questions[currentQuestionIndex].question}</p>
            </div>

              <div className="flex flex-col mb-4">
                {questions[currentQuestionIndex].options.map((option, index) => (
                  <label key={index} className="mb-2">
                    <input
                      type="radio"
                      name={`question-${currentQuestionIndex}`}
                      value={option}
                      onChange={handleAnswerChange}
                      className="mr-2"
                    />
                    {option}
                  </label>
                ))}
              </div>

            <div className="flex justify-end space-x-4 mb-4">
              <button
                onClick={handleSaveForLater}
                className="px-4 py-2 bg-gray-500 text-white rounded"
              >
                Save for Later
              </button>
              <button
                onClick={handleNext}
                className="px-4 py-2 bg-blue-500 text-white rounded"
              >
                Next
              </button>
            </div>
            {showWarning && (
              <div className="flex items-center p-3 bg-red-500 text-white rounded-lg shadow-md w-80">
                <div className="mr-2">
                  <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="none" viewBox="0 0 24 24">
                    <path fill="#fff" d="M13 13h-2v-6h2zm0 4h-2v-2h2zm-1-15c-1.3132 0-2.61358.25866-3.82683.7612-1.21326.50255-2.31565 1.23915-3.24424 2.16773-1.87536 1.87537-2.92893 4.41891-2.92893 7.07107 0 2.6522 1.05357 5.1957 2.92893 7.0711.92859.9286 2.03098 1.6651 3.24424 2.1677 1.21325.5025 2.51363.7612 3.82683.7612 2.6522 0 5.1957-1.0536 7.0711-2.9289 1.8753-1.8754 2.9289-4.4189 2.9289-7.0711 0-1.3132-.2587-2.61358-.7612-3.82683-.5026-1.21326-1.2391-2.31565-2.1677-3.24424-.9286-.92858-2.031-1.66518-3.2443-2.16773-1.2132-.50254-2.5136-.7612-3.8268-.7612z"></path>
                  </svg>
                </div>
                <div className="flex-1 font-medium text-sm">
                  Please give the answer
                </div>
              </div>
            )}
          </div>
        ) : (
          <div className="w-full">
            <h2 className="text-xl font-bold mb-4">Quiz Results</h2>
            <p className="text-lg mb-4">Your Score: {calculateScore()} / {questions.length}</p>
            <div>
              {questions.map((question, index) => (
                <div key={index} className="mb-4">
                  <p className="text-lg">
                    {index + 1}. {question.question}
                  </p>
                  <p className={`text-sm ${question.answer === userAnswers[index] ? 'text-green-500' : 'text-red-500'}`}>
                    Your Answer: {userAnswers[index]}
                  </p>
                  {question.answer !== userAnswers[index] && (
                    <p className="text-sm text-blue-500">Correct Answer: {question.answer}</p>
                  )}
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
      :
      <div>
      no quiz
      </div>
    }

      </>
  );
};

export default QuestionComponent;
